/*-----------------------------------------------------------------------------
|| Tabellen-Skript der Tabelle TST_RUN
*/-----------------------------------------------------------------------------


  CREATE TABLE "DWH_MTD"."TST_RUN"
   (	"CONFIG" NUMBER NOT NULL ENABLE,
	"DELTA_DATE" DATE,
	"RUN_BEGIN" TIMESTAMP (6),
	"RUN_END" TIMESTAMP (6),
	"RESULT" NUMBER,
	"GRADE" VARCHAR2(255 CHAR),
	"ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE,
	"ERROR" VARCHAR2(256 CHAR),
	"REMARK" VARCHAR2(1024 CHAR),
	"SQL_ID" VARCHAR2(13 CHAR),
	"SQL_PLAN" CLOB,
	 CONSTRAINT "TST_RUN_PK" PRIMARY KEY ("ID")
  USING INDEX PCTFREE 10 INITRANS 2 MAXTRANS 255 COMPUTE STATISTICS
  TABLESPACE "DWH_MTD"  ENABLE
   ) SEGMENT CREATION IMMEDIATE
  PCTFREE 10 PCTUSED 40 INITRANS 1 MAXTRANS 255
 NOCOMPRESS LOGGING
  TABLESPACE "DWH_MTD"
 LOB ("SQL_PLAN") STORE AS SECUREFILE (
  TABLESPACE "DWH_MTD" ENABLE STORAGE IN ROW CHUNK 8192
  NOCACHE LOGGING  NOCOMPRESS  KEEP_DUPLICATES ) ;

-------------------
-- Zugehörige Kommentare
-------------------

   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."CONFIG" IS 'Configuration to use. FK to TST_CNF';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."DELTA_DATE" IS 'Optional: Delta_Datum used';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."RUN_BEGIN" IS 'Begin of test run';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."RUN_END" IS 'End of test run';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."RESULT" IS 'Result of test (numeric)';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."GRADE" IS 'Grade (PASSED_ERROR)';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."ID" IS 'Number of this run (PK)';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."ERROR" IS 'Error text if occured';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."REMARK" IS 'Remarks and Result';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."SQL_ID" IS 'SQLID of executed SQL';
   COMMENT ON COLUMN "DWH_MTD"."TST_RUN"."SQL_PLAN" IS 'SQL plan text';
   COMMENT ON TABLE "DWH_MTD"."TST_RUN"  IS 'Test runs';

